import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [favorites, setFavorites] = useState([]);

  // Load theme and favorites from localStorage on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem('realMadridTheme');
    const savedFavorites = localStorage.getItem('realMadridFavorites');

    if (savedTheme) {
      setIsDarkMode(JSON.parse(savedTheme));
    }

    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // Save theme to localStorage when it changes
  useEffect(() => {
    localStorage.setItem('realMadridTheme', JSON.stringify(isDarkMode));
    document.body.className = isDarkMode ? 'dark-theme' : 'light-theme';
  }, [isDarkMode]);

  // Save favorites to localStorage when they change
  useEffect(() => {
    localStorage.setItem('realMadridFavorites', JSON.stringify(favorites));
  }, [favorites]);

  const toggleTheme = () => {
    setIsDarkMode(prev => !prev);
  };

  const addToFavorites = (playerId) => {
    setFavorites(prev => {
      if (prev.includes(playerId)) {
        return prev.filter(id => id !== playerId);
      } else {
        return [...prev, playerId];
      }
    });
  };

  const isFavorite = (playerId) => {
    return favorites.includes(playerId);
  };

  const value = {
    isDarkMode,
    toggleTheme,
    favorites,
    addToFavorites,
    isFavorite
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};
