import React from 'react';
import { players } from '../data/players';
import './StatisticsChart.css';

const StatisticsChart = () => {
  // Calculate team statistics
  const totalGoals = players.reduce((sum, player) => sum + player.goals, 0);
  const totalAssists = players.reduce((sum, player) => sum + player.assists, 0);
  const totalMatches = players.reduce((sum, player) => sum + player.matchesPlayed, 0);
  const avgGoalsPerMatch = (totalGoals / totalMatches).toFixed(2);
  const avgAssistsPerMatch = (totalAssists / totalMatches).toFixed(2);

  // Get top performers
  const topScorer = players.reduce((prev, current) =>
    (prev.goals > current.goals) ? prev : current
  );

  const topAssistant = players.reduce((prev, current) =>
    (prev.assists > current.assists) ? prev : current
  );

  const mostExperienced = players.reduce((prev, current) =>
    (prev.matchesPlayed > current.matchesPlayed) ? prev : current
  );

  // Position distribution
  const positionStats = players.reduce((acc, player) => {
    acc[player.position] = (acc[player.position] || 0) + 1;
    return acc;
  }, {});

  // Goals distribution by position
  const goalsByPosition = players.reduce((acc, player) => {
    acc[player.position] = (acc[player.position] || 0) + player.goals;
    return acc;
  }, {});

  return (
    <div className="statistics-chart">
      <h2>üèÜ Team Statistics</h2>

      <div className="stats-overview">
        <div className="stat-card">
          <div className="stat-icon">‚öΩ</div>
          <div className="stat-value">{totalGoals}</div>
          <div className="stat-label">Total Goals</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üéØ</div>
          <div className="stat-value">{totalAssists}</div>
          <div className="stat-label">Total Assists</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìä</div>
          <div className="stat-value">{totalMatches}</div>
          <div className="stat-label">Total Matches</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìà</div>
          <div className="stat-value">{avgGoalsPerMatch}</div>
          <div className="stat-label">Avg Goals/Match</div>
        </div>
      </div>

      <div className="top-performers">
        <h3>‚≠ê Top Performers</h3>
        <div className="performer-cards">
          <div className="performer-card">
            <img src={topScorer.photo} alt={topScorer.name} className="performer-photo" />
            <div className="performer-info">
              <h4>{topScorer.name}</h4>
              <p className="performer-stat">‚öΩ {topScorer.goals} Goals</p>
              <span className="performer-badge">Top Scorer</span>
            </div>
          </div>
          <div className="performer-card">
            <img src={topAssistant.photo} alt={topAssistant.name} className="performer-photo" />
            <div className="performer-info">
              <h4>{topAssistant.name}</h4>
              <p className="performer-stat">üéØ {topAssistant.assists} Assists</p>
              <span className="performer-badge">Top Assistant</span>
            </div>
          </div>
          <div className="performer-card">
            <img src={mostExperienced.photo} alt={mostExperienced.name} className="performer-photo" />
            <div className="performer-info">
              <h4>{mostExperienced.name}</h4>
              <p className="performer-stat">üìä {mostExperienced.matchesPlayed} Matches</p>
              <span className="performer-badge">Most Experienced</span>
            </div>
          </div>
        </div>
      </div>

      <div className="position-breakdown">
        <h3>üìä Position Breakdown</h3>
        <div className="position-charts">
          <div className="chart-section">
            <h4>Players by Position</h4>
            <div className="position-bars">
              {Object.entries(positionStats).map(([position, count]) => (
                <div key={position} className="position-bar">
                  <span className="position-label">{position}</span>
                  <div className="bar-container">
                    <div
                      className="bar-fill"
                      style={{
                        width: `${(count / players.length) * 100}%`,
                        backgroundColor: getPositionColor(position)
                      }}
                    ></div>
                  </div>
                  <span className="position-count">{count}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="chart-section">
            <h4>Goals by Position</h4>
            <div className="position-bars">
              {Object.entries(goalsByPosition).map(([position, goals]) => (
                <div key={position} className="position-bar">
                  <span className="position-label">{position}</span>
                  <div className="bar-container">
                    <div
                      className="bar-fill"
                      style={{
                        width: `${(goals / totalGoals) * 100}%`,
                        backgroundColor: getPositionColor(position)
                      }}
                    ></div>
                  </div>
                  <span className="position-count">{goals}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const getPositionColor = (position) => {
  switch (position.toLowerCase()) {
    case 'forward': return '#ff4757';
    case 'midfielder': return '#3742fa';
    case 'defender': return '#2ed573';
    case 'goalkeeper': return '#ffa502';
    default: return '#ff4757';
  }
};

export default StatisticsChart;
